    apiKey: "AIzaSyA-PVzZ6DCFm1__pYgih8rxw3bSlcNozPk",
    authDomain: "react-blog-hook.firebaseapp.com",
    projectId: "react-blog-hook",
    storageBucket: "react-blog-hook.appspot.com",
    messagingSenderId: "568539223310",
    appId: "1:568539223310:web:beaef62335ba1fb185b35f"
};

